	case "stop":
		context.loops = context.loops + 1;
		msg.payload = "stopped";
		context.loop = "stop";
		return [msg,{payload:"reset",reset:"reset",delay:0}];
	case "toggle":
		if (context.loop == "start") {
			msg.payload = "stopped";
			context.loop = "stop";
			return [msg,{payload:"set delay",delay:500}];
		} else {
			msg.payload = "started";
			context.loop = "loop";
			context.loops = 1;
