function formatTemp(high, low){
    if (units == "imperial") {
        if (low){
          temp = parseFloat(high).toFixed() + '/' + parseFloat(low).toFixed()
        }
        else {
          temp = parseFloat(high).toFixed() + 'F'
        }
    }
    else { // metric
//        temp = parseFloat(temp).toFixed(1) + 'C'
        if (low){
          temp = parseFloat(high).toFixed() + '/' + parseFloat(low).toFixed()
        }
        else {
          temp = parseFloat(high).toFixed() + 'C'
        }
    }
    return temp;
}
function dayName(unixTime){
    var d = new Date(unixTime * 1000);
    var weekday = new Array(7);
        weekday[0] = "Sun";
        weekday[1] = "Mon";
        weekday[2] = "Tue";
        weekday[3] = "Wed";
        weekday[4] = "Thu";
        weekday[5] = "Fri";
        weekday[6] = "Sat";

    return weekday[d.getDay()]
}
function timeConvert(UNIX_timestamp){
  var a = new Date(UNIX_timestamp * 1000);
  var hour = a.getHours();
  var suffix = " am";
  if (hour >= 12) {
    hour = hour - 12;
    suffix = " pm";
  }
  if (hour === 0) {
    hour = 12;
  }
  var min = a.getMinutes();
  if (min < 10) {min = "0" + min;}
//  return hour + ':' + min + suffix;
  return hour + suffix;
}
