var lowBytes = msg.payload.data[0];
var highBytes = msg.payload.data[1];
var array = [];
for ( i = 0 ; i < 32; i++)
{
   if ( i < 16)
   {
       if ( highBytes % 2 == 0)
       {
           array[i] = 0;
       } else {
           array[i] = 1;
       }
       highBytes = Math.floor(highBytes / 2);
   } else {
       if ( lowBytes % 2 == 0)
       {
           array[i] = 0;
       } else {
           array[i] = 1;
       }
       lowBytes = Math.floor(lowBytes / 2);
   }
}
var sign = Math.pow((-1),array[31]);
var e = 1 * array[23] + 2 * array[24] + 4 * array[25] + 8 * array[26] + 16 * array[27] + 32 * array[28] + 64 * array[29] + 128 * array[30];
var matissa = 1 + array[22] * Math.pow(2,-1) + array[21] * Math.pow(2,-2) + array[20] * Math.pow(2,-3) + array[19] * Math.pow(2,-4) + array[18] * Math.pow(2,-5) + array[17] * Math.pow(2,-6) + array[16] * Math.pow(2,-7) + array[15] * Math.pow(2,-8) + array[14] * Math.pow(2,-9) + array[13] * Math.pow(2,-10) + array[12] * Math.pow(2,-11) + array[11] * Math.pow(2,-12) + array[10] * Math.pow(2,-13) + array[9] * Math.pow(2,-14) + array[8] * Math.pow(2,-15) + array[7] * Math.pow(2,-16) + array[6] * Math.pow(2,-17) + array[5] * Math.pow(2,-18) + array[4] * Math.pow(2,-19) + array[3] * Math.pow(2,-20) + array[2] * Math.pow(2,-21) + array[1] * Math.pow(2,-22) + array[0] * Math.pow(2,-23);
var value = sign * Math.pow(2,(e-127)) * matissa;
msg.payload = value;
return msg;

const schedule = msg.payload;
if(typeof msg.payload === "undefined") return null;
let scheduled = context.scheduled || [];
let todelete = [];
scheduled.forEach((item,index) => {
    if(!schedule.some(element => JSON.stringify(element) == JSON.stringify(item.schedule))){
        clearTimeout(item.timer);
        todelete.push(index);
    }
})
scheduled = scheduled.filter((item,index) => !todelete.includes(index));
context.scheduled = scheduled;
schedule.forEach(element => {
    const execute = element;
    const time = new Date();
    const timestamp = time.getTime();
    const hour = time.getHours();
    const minute = time.getMinutes();
    const year = time.getFullYear();
    const month = time.getMonth();
    const day = time.getDate();
    const inputS = execute.time.split(":");
    const hourS = parseInt(inputS[0]);
    const minuteS = parseInt(inputS[1]);
    if(typeof hourS != "number" || typeof minuteS != "number") return null;
    const timeS = new Date(year, month, day, hourS, minuteS);
    const timestampS = timeS.getTime();
    let timestampD = 0;
    if(timestampS >= timestamp){
        timestampD = timestampS - timestamp;
    } else {
        timestampD = (timestampS + 86400000) - timestamp;
    }
    let oldscheduled = context.scheduled;
    if(!oldscheduled.some(element => JSON.stringify(element.schedule) == JSON.stringify(execute))){
        const newtimer = setTimeout(()=>{
            let newscheduled = context.scheduled;
            const deleteindex = newscheduled.indexOf(newschedule);
            newscheduled.splice(deleteindex,1);
            node.send({payload:newschedule.schedule});
            context.scheduled = newscheduled;
        },timestampD);
        const newschedule = {
            runtime: timestampD,
            timer: newtimer,
            schedule: execute
        };
        oldscheduled.push(newschedule);
        context.scheduled = oldscheduled;
    }
});
const sendscheduled = context.scheduled;
