let date = new Date()
timeZoneOffset = date.getTimezoneOffset() *60;

function recalculateTime(x){
    var z = new Date(x).valueOf();
    var adjustedTime = (z/1000 + (-1* timeZoneOffset))*1000;
    return new Date(adjustedTime);
}

function secondsToHms(d) {
    d = Number(d);

    var h = Math.floor(d / 3600);
    var m = Math.floor(d % 3600 / 60);

    return ('0' + h).slice(-2) + "h " + ('0' + m).slice(-2)+"min";
}

function stringtodate(timenow){
    let date1 = Date.parse(timenow);
    return date1;

}



